mapper.insert:
insert into t_whs_order_refund_application(
application_type, exist_problem, other_problem, refund_evidence, audit_status, order_discount_fee, delivery_fee, comments, order_id, user_id, contacts_phone, create_at
)VALUES (
#{applicationType}, #{existProblem}, #{otherProblem}, #{refundEvidence}, #{auditStatus}, #{orderDiscountFee}, #{deliveryFee}, #{comments}, #{orderId}, #{userId}, #{contactsPhone}, now()
)

mapper.select:
SELECT 
FROM t_whs_order t1
WHERE t1.order_code = #{0}

mapper.update:
update t_whs_order_refund_application
id = #{id}, application_type = #{applicationType}, exist_problem = #{existProblem}, other_problem = #{otherProblem}, refund_evidence = #{refundEvidence}, audit_status = #{auditStatus}, order_discount_fee = #{orderDiscountFee}, delivery_fee = #{deliveryFee}, comments = #{comments}, order_id = #{orderId}, user_id = #{userId},
where id=#{id}

mapper.select:
select a.id,a.order_id,o.create_time,o.total_fee,o.discount_fee,o.payable_fee,l.payment_type,l.payment_time,o.pay_status,
u.shop_name,u.user_name,u.phone,o.order_status,o.hd_status,a.audit_status
from t_whs_order_refund_application a 
LEFT JOIN t_whs_order o on a.order_id=o.order_code
LEFT JOIN t_whs_payment_log l on l.order_code=o.order_code
LEFT JOIN t_whs_user u on u.id=o.user_id


		and date_format(a.create_at,'%Y-%m-%d') >= #{createTimeBegin}


		and date_format(a.create_at,'%Y-%m-%d') <= #{createTimeEnd}


		and o.order_status = #{orderStatus}


		and o.pay_status = #{payStatus}


		and o.settlement_type = #{settlementType}


		and o.order_code like CONCAT('%',#{orderCode},'%')


		and u.phone like CONCAT('%',#{phone},'%')

select COUNT(*)
from t_whs_order_refund_application a LEFT JOIN t_whs_order o on a.order_id=o.order_code
LEFT JOIN t_whs_payment_log l on l.order_code=o.order_code
LEFT JOIN t_whs_user u on u.id=o.user_id


		and date_format(a.create_at,'%Y-%m-%d') >= #{createTimeBegin}


		and date_format(a.create_at,'%Y-%m-%d') <= #{createTimeEnd}


		and o.order_status = #{orderStatus}


		and o.pay_status = #{payStatus}


		and o.settlement_type = #{settlementType}


		and o.order_code like CONCAT('%',#{orderCode},'%')


		and u.phone like CONCAT('%',#{phone},'%')
