mapper.update:
update t_whs_coupon_config
coupon_id=#{couponId}, effective_time=#{effectiveTime}, failure_time=#{failureTime}, vaild_days=#{vaildDays}, coupon_des=#{couponDes},
where id = #{id}

mapper.select:
select count(id) from t_whs_coupon_config 


		and coupon_name like concat('%',#{couponName},'%')

	and coupon_type=#{couponType}

select  from t_whs_coupon_config


		and coupon_name like concat('%',#{couponName},'%')

	and coupon_type=#{couponType}
