mapper.update:
update t_whs_coupon_entity
use_time=#{useTime}, coupon_status=#{couponStatus},
where id = #{id}

mapper.select:
select a.id,a.coupon_config_id as couponConfigId,a.user_id as userId, DATE_FORMAT(a.effective_time,'%Y-%m-%d %H:%i:%s') as effectiveTime, DATE_FORMAT(a.failure_time,'%Y-%m-%d %H:%i:%s') as failureTime, a.coupon_status as couponStatus, DATE_FORMAT(a.get_time,'%Y-%m-%d %H:%i:%s') as getTime, DATE_FORMAT(a.use_time,'%Y-%m-%d %H:%i:%s') as userTime, a.coupon_fee as couponFee,a.full_fee as fullFee,c.coupon_name as couponName,b.coupon_des as couponDes from t_whs_coupon_entity a left join t_whs_coupon_config b on a.coupon_config_id = b.id left join t_whs_coupon_plate c on b.coupon_id = c.id
and user_id = #{userId} and coupon_status = #{couponStatus}
order by couponFee desc
limit #{start},#{rows}
