mapper.insert:
insert into t_whs_goods(
goods_code, goods_name, category_id, goods_image, base_bar, base_unit, stock_limit, goods_des, goods_images, other_image,
) values(
#{goodsCode}, #{goodsName}, #{categoryId}, #{goodsImage}, #{baseBar}, #{baseUnit}, #{stockLimit}, #{goodsDes}, #{goodsImages}, #{otherImage},
)

mapper.update:
update t_whs_goods
goods_code=#{goodsCode}, goods_name=#{goodsName}, category_id=#{categoryId}, goods_image=#{goodsImage}, base_bar=#{baseBar}, base_unit=#{baseUnit}, stock_limit=#{stockLimit}, goods_des=#{goodsDes}, goods_images=#{goodsImages}, other_image=#{otherImage},
where id = #{id}
