mapper.insert:
insert into t_whs_goods(
goods_code, goods_name, category_id, goods_image, base_bar, base_unit, stock_limit, goods_des, goods_images, other_image,
) values(
#{goodsCode}, #{goodsName}, #{categoryId}, #{goodsImage}, #{baseBar}, #{baseUnit}, #{stockLimit}, #{goodsDes}, #{goodsImages}, #{otherImage},
)

mapper.update:
update t_whs_goods
goods_code=#{goodsCode}, goods_name=#{goodsName}, category_id=#{categoryId}, goods_image=#{goodsImage}, base_bar=#{baseBar}, base_unit=#{baseUnit}, stock_limit=#{stockLimit}, stock_limit=stock_limit-#{reduceStockLimit}, goods_des=#{goodsDes}, goods_images=#{goodsImages}, other_image=#{otherImage},
where id = #{id}

mapper.select:
select count(t1.id) from t_whs_goods t1

	and t1.goods_code=#{goodsCode}
	and t1.category_id = #{categoryId}
	and t1.goods_name like CONCAT('%',#{goodsName},'%')

select special_price,limit_quantity,activity_id from t_whs_flashsale_goods

	and goods_standard_id=#{goodsStandardId}
	and activity_id=#{activityId}

select g.id,g.goods_name,s.price,(select MAX(price) FROM t_whs_goods_price_region r where r.goods_standard_id=s.id) maxPrice, (select MIN(price) from t_whs_goods_price_region r where r.goods_standard_id=s.id) minPrice,u.unit_name, (select count(*) from t_whs_order_goods og where og.goods_standard_id=s.id) saleCount,s.standard,g.goods_image,s.id as standardId from t_whs_goods_standard s LEFT JOIN t_whs_goods g on g.id=s.goods_id LEFT JOIN t_whs_goods_unit u on u.unit_code=s.unit_code where s.vaild = 'yes' and s.id in
#{plateId}
LIMIT 5
