mapper.insert:
insert into t_whs_goods_price_region(
goods_standard_id, price, standard_des, min_quantity, max_quantity, reorder,
) values(
#{goodsStandardId}, #{price}, #{standardDes}, #{minQuantity}, #{maxQuantity}, #{reorder},
)

mapper.update:
update t_whs_goods_price_region
goods_standard_id = #{goodsStandardId}, price = #{price}, standard_des = #{standardDes}, min_quantity = #{minQuantity}, max_quantity = #{maxQuantity}, reorder = #{reorder},
where id = #{id}

mapper.select:
select count(t1.id) from t_whs_goods_price_region t1 
left join t_whs_goods_standard t2 on t1.goods_standard_id = t2.id
left join t_whs_goods t3 on t2.goods_id = t3.id


		and t1.price >= #{minPrice}


		and t1.price <= #{maxPrice}


		and t3.goods_name like concat('%',#{goodsName},'%')

select ,t2.standard,t3.goods_name as goodsName from t_whs_goods_price_region t1 left join t_whs_goods_standard t2 on t1.goods_standard_id = t2.id left join t_whs_goods t3 on t2.goods_id = t3.id
and t1.price >= #{minPrice}
and t1.price <= #{maxPrice}
and t3.goods_name like concat('%',#{goodsName},'%')
