mapper.insert:
insert into t_whs_goods_standard(
goods_id, bar_code, weight, unit_code, standard, price, vaild, hd_sku_id, standard_desc, purchase_price,
) values(
#{goodsId}, #{barCode}, #{weight}, #{unitCode}, #{standard}, #{price}, #{vaild}, #{hdSkuId}, #{standardDesc}, #{purchasePrice},
)

mapper.update:
update t_whs_goods_standard
goods_id=#{goodsId}, bar_code=#{barCode}, weight=#{weight}, unit_code=#{unitCode}, standard=#{standard}, price=#{price}, vaild=#{vaild}, hd_sku_id=#{hdSkuId}, standard_desc=#{standardDesc}, purchase_price=#{purchasePrice},
where id = #{id}

mapper.select:
select count(t1.id) from t_whs_goods_standard t1
left join t_whs_goods t2 on t1.goods_id = t2.id
left join t_whs_goods_unit t3 on t1.unit_code = t3.id
left join t_whs_goods_category t4 on t2.category_id = t4.id

	and t1.bar_code=#{goodsCode}
	and t2.goods_name like CONCAT('%',#{goodsName},'%')
	and t1.vaild = #{vaild}
	and t1.price >= #{minPrice}
	and t1.price <= #{maxPrice}

select , ,,

from t_whs_goods_standard t1
left join t_whs_goods t2 on t1.goods_id = t2.id
left join t_whs_goods_unit t3 on t1.unit_code = t3.id
left join t_whs_goods_category t4 on t2.category_id = t4.id

	and t1.bar_code=#{goodsCode}
	and t2.goods_name like CONCAT('%',#{goodsName},'%')
	and t1.vaild = #{vaild}
	and t1.price >= #{minPrice}
	and t1.price <= #{maxPrice}
