mapper.insert:
insert into t_whs_invoice_title(
id, user_id, taxpayer_number, company_name, address_detail, address_area, contact_name, contact_phone, bank_name, bank_card_code,
) values(
#{id}, #{userId}, #{taxpayerNumber}, #{companyName}, #{addressDetail}, #{addressArea}, #{contactName}, #{contactPhone}, #{bankName}, #{bankCardCode},
)

mapper.select:
select id,invoice_title_id invoiceTitleId,taxpayer_number taxpayerNumber,company_name companyName,contact_name contactName,
contact_phone contactName,contact_phone contactPhone,invoice_fee invoiceFee,invoice_tax invoiceTax,tax_fee taxFee,address_area addressArea,
address_detail addressDetail,bank_name bankName,bank_card_code bankCardCode,create_time createTime,invoice_status invoiceStatus,
invoice_print_time invoicePrintTime,invoice_order_ids invoiceOrderIds,invoice_code invoiceCode,user_id userId
from t_whs_invoice

     and id = #{id} 
     and invoice_title_id = #{invoiceTitleId} 
     and taxpayer_number = #{taxpayerNumber} 
     and company_name = #{companyName} 
     and contact_name = #{contactName} 
     and contact_phone = #{contactPhone} 
     and address_area = #{addressArea} 
     and address_detail = #{addressDetail} 
     and bank_name = #{bankName} 
     and bank_card_code = #{bankCardCode} 
     and create_time = #{createTime} 
     and invoice_print_time = #{invoicePrintTime} 
     and invoice_order_ids = #{invoiceOrderIds} 
     and invoice_code = #{invoiceCode} 
     and user_id = #{userId}

SELECT 
FROM t_whs_invoice t1
WHERE t1.id = #{0}

select count(t1.id) from t_whs_invoice t1

    and t1.invoice_status = #{invoiceStatus}
    and t1.create_time >= #{createTimeBegin}
    and t1.create_time <= #{createTimeEnd}

select 
from t_whs_invoice t1

    and t1.invoice_status = #{invoiceStatus}
    and t1.create_time >= #{createTimeBegin}
    and t1.create_time <= #{createTimeEnd}

mapper.update:
UPDATE t_whs_invoice
SET invoice_status = "yes"
where id=#{id}
