mapper.select:
select id,order_code orderCode,discount_fee discountFee,settlement_type settlementType,pay_status payStatus
 ,order_status orderStatus,create_time createTime,total_fee totalFee,payable_fee payableFee,remarks,after_sale_time afterSaleTime
from t_whs_order o

     and o.user_id=#{userId}
     and o.settlement_type=#{settlementType}
     and o.pay_status=#{payStatus}
     and o.order_status = #{orderStatus}

select id,order_code orderCode,order_discount_fee orderDiscountFee,order_type orderType,pay_status payStatus
,order_status orderStatus,order_create_time createTime,order_total total,order_need_fee needPay,remarks,after_sale_time afterSaleTime
from t_whs_order o

    and o.user_id=#{userId}
    and o.order_type=#{orderType}
    and o.pay_status=#{payStatus}
    and o.order_status = #{orderStatus}

and DATE_SUB(CURDATE(),INTERVAL 30 DAY) <= o.order_create_time

select * from t_whs_order

     and id = #{id} 
     and order_code = #{orderCode} 
     and user_id = #{userId} 
     and salesman_id = #{salesmanId} 
     and create_time = #{createTime}
