mapper.insert:
insert into t_whs_param_category(
param_category_name, param_category_code, show_type, parent_id,
) values(
#{paramCategoryName}, #{paramCategoryCode}, #{showType}, #{parentId},
)

mapper.select:
select count(id) from t_whs_param_category


		and param_category_name like CONCAT('%',#{paramCategoryName},'%') 


		and parent_id = #{parentId}

select * from t_whs_param_category
and param_category_name like CONCAT('%',#{paramCategoryName},'%')
and parent_id = #{parentId}
