mapper.insert:
insert into t_whs_salesuser_relation(
id, user_id, salesman_id, audit_status,
) values(
#{id}, #{userId}, #{salesmanId}, #{auditStatus},
)

mapper.select:
select * from t_whs_salesuser_relation


        and id = #{id}


        and user_id =#{userId}


        and salesman_id =#{salesmanId}


        and audit_status =#{auditStatus}

mapper.insert:
insert into t_whs_salesuser(
 id, open_id, union_id, salesman_name, salesman_phone, salesman_password, salesman_head_image, create_person, create_at, invite_code,account,sales_status, 
)VALUES (
 #{id}, #{openId}, #{unionId}, #{salesmanName}, #{salesmanPhone}, #{salesmanPassword}, #{salesmanHeadImage}, #{createPerson}, now(), #{inviteCode}, #{account},#{salesStatus},
)

mapper.update:
update t_whs_salesuser
id = #{id}, open_id = #{openId}, union_id = #{unionId}, salesman_name = #{salesmanName}, salesman_phone = #{salesmanPhone}, salesman_password = #{salesmanPassword}, salesman_head_image = #{salesmanHeadImage}, create_person = #{createPerson}, create_at = #{createAt}, invite_code = #{inviteCode}, account = #{account}, sales_status = #{salesStatus}
where id=#{id}

mapper.select:
select * from t_whs_salesuser

     and id = #{id} 
     and open_id = #{openId} 
     and union_id = #{unionId} 
     and salesman_name = #{salesmanName} 
     and salesman_phone = #{salesmanPhone} 
     and salesman_password = #{salesmanPassword} 
     and salesman_head_image = #{salesmanHeadImage} 
     and create_person = #{createPerson} 
     and create_at = #{createAt} 
     and invite_code = #{inviteCode} 
     and account = #{account} 
     and sales_status = #{salesStatus}

select * from t_whs_salesuser

       and id = #{id} 
       and open_id = #{openId} 
       and union_id = #{unionId} 
       and salesman_name like CONCAT('%',#{salesmanName},'%') 
       and salesman_phone like CONCAT('%',#{salesmanPhone},'%') 
       and salesman_password = #{salesmanPassword} 
       and salesman_head_image = #{salesmanHeadImage} 
       and create_person = #{createPerson} 
       and create_at = #{createAt} 
       and invite_code = #{inviteCode} 
       and account = #{account} 
       and sales_status = #{salesStatus}

select count(id) from t_whs_salesuser

