mapper.insert:
insert into t_whs_user_address(
sex, phone, address_area, address_detail, is_default, user_id, contacts_name,
) values(
#{sex}, #{phone}, #{addressArea}, #{addressDetail}, #{isDefault}, #{userId}, #{userId},
)

mapper.select:
select * from t_whs_user
where phone in

	#{item}

select * from t_whs_user
where id in

    #{item}

select * from t_whs_user
where id in

	#{item}

select  t1.id,phone,shop_name,user_name,city,address_detail address,register_time,balance,profile_photo,user_status
from t_whs_user t1 left join t_whs_salesuser_relation t2 on t1.id = t2.user_id

    and t1.user_name = #{userName}
    and t2.salesman_id = #{salesUserId}
    and t1.phone = #{phone}

        and t1.id in

            #{item}

select count(t1.id)
from t_whs_user t1 left join t_whs_salesuser_relation t2 on t1.id = t2.user_id

    and t1.user_name = #{userName}
    and t2.salesman_id = #{salesUserId}
    and t1.phone = #{phone}

        and t1.id in

            #{item}

select  t1.id
from t_whs_user t1 left join t_whs_salesuser_relation t2 on t1.id = t2.user_id

    and t1.user_name = #{userName}
    and t2.salesman_id = #{salesUserId}
    and t1.phone = #{phone}

SELECT u.id,u.phone,u.shop_name,u.user_name,u.register_time,s.salesman_name,t1.thisMonth,t2.lastMonth, t3.accumulative,t4.arrears,t5.lastCreateTime, CASE u.user_status WHEN 'uncertified' THEN '未认证' WHEN 'unaudited' THEN '认证中待审核' WHEN 'certified' THEN '已认证' END AS user_status FROM t_whs_user u LEFT JOIN ( SELECT user_id, CONCAT('￥',CONVERT(FORMAT(SUM(payable_fee)/100.0,2),CHAR),'/',CONVERT(COUNT(id),CHAR),'次') as thisMonth FROM t_whs_order WHERE order_status = 'received' AND DATE_FORMAT(NOW(),'%Y-%m') = DATE_FORMAT(create_time,'%Y-%m') GROUP BY user_id ) as t1 ON u.id = t1.user_id LEFT JOIN ( SELECT user_id, CONCAT('￥',CONVERT(FORMAT(SUM(payable_fee)/100.0,2),CHAR),'/',CONVERT(COUNT(id),CHAR),'次') as lastMonth FROM t_whs_order WHERE order_status = 'received' AND date_format(create_time,'%Y-%m')=date_format(DATE_SUB(NOW(), INTERVAL 1 MONTH),'%Y-%m') GROUP BY user_id ) as t2 ON u.id = t2.user_id LEFT JOIN ( SELECT user_id, CONCAT('￥',CONVERT(FORMAT(SUM(payable_fee)/100.0,2),CHAR),'/',CONVERT(COUNT(id),CHAR),'次') as accumulative FROM t_whs_order WHERE order_status = 'received' GROUP BY user_id ) as t3 ON u.id = t3.user_id LEFT JOIN ( SELECT user_id, CONCAT('￥',CONVERT(FORMAT(SUM(payable_fee)/100.0,2),CHAR),'/',CONVERT(COUNT(id),CHAR),'次') as arrears FROM t_whs_order WHERE settlement_type = 'offline' and pay_status = 'unpaid' GROUP BY user_id ) as t4 ON u.id = t4.user_id LEFT JOIN( SELECT user_id, MAX(create_time) as lastCreateTime FROM t_whs_order WHERE order_status = 'received' GROUP BY user_id ) as t5 on u.id = t5.user_id LEFT JOIN t_whs_salesuser_relation sr ON u.id = sr.user_id LEFT JOIN t_whs_salesuser s ON sr.salesman_id = s.id
and u.user_name like concat('%',#{userName},'%') and u.phone like concat('%',#{phone},'%') and s.id = #{saleId} and date_format(u.register_time,'%Y-%m-%d) >= #{beginTime} and date_format(u.register_time,'%Y-%m-%d) <=#{endTime}
and date_format(u.register_time,'%Y-%m') = date_format(now(),'%Y-%m')

SELECT count(u.id)
FROM t_whs_user u
LEFT JOIN t_whs_salesuser_relation sr ON u.id = sr.user_id
LEFT JOIN t_whs_salesuser s ON sr.salesman_id = s.id

	and u.user_name like concat('%',#{userName},'%')
	and u.phone like concat('%',#{phone},'%')
	and s.id = #{saleId}
	and date_format(u.register_time,'%Y-%m-%d) >= #{beginTime}
	and date_format(u.register_time,'%Y-%m-%d) <=#{endTime}

SELECT u.id,u.phone,u.city,u.register_time,u.nickname,u.open_id,CONCAT('￥-',CONVERT(FORMAT(u.balance/100.0,2),CHAR)) as balance, u.union_id,u.profile_photo,u.shop_name,u.user_name,u.address_detail, t1.arrears, CASE u.sex WHEN 'uncertified' THEN '未认证' WHEN 'female' THEN '女' WHEN 'male' THEN '男' WHEN 'unknown' THEN '未知' END AS sex, CASE u.user_status WHEN 'uncertified' THEN '未认证' WHEN 'unaudited' THEN '认证中待审核' WHEN 'certified' THEN '已认证' END AS user_status FROM t_whs_user u LEFT JOIN ( SELECT user_id, CONCAT('￥-',CONVERT(FORMAT(SUM(payable_fee)/100.0,2),CHAR)) as arrears FROM t_whs_order WHERE settlement_type = 'offline' and pay_status = 'unpaid' GROUP BY user_id ) as t1 ON u.id = t1.user_id LEFT JOIN t_whs_salesuser_relation sr ON u.id = sr.user_id LEFT JOIN t_whs_salesuser s ON sr.salesman_id = s.id
and u.user_name like concat('%',#{userName},'%') and u.phone like concat('%',#{phone},'%') and s.id = #{saleId} and date_format(u.register_time,'%Y-%m-%d) >= #{beginTime} and date_format(u.register_time,'%Y-%m-%d) <=#{endTime}
and date_format(u.register_time,'%Y-%m') = date_format(now(),'%Y-%m')
